angular.module('angularSuperGallery').run(['$templateCache', function($templateCache) {$templateCache.put('views/angular-super-gallery.html','<div class="gallery-view {{ $ctrl.id }}" data-ng-class="{\'nogui\' : !$ctrl.gui}" data-ng-click="$ctrl.setFocus()" data-ng-show="$ctrl.visible" data-ng-cloak>\r\n\r\n    <div tabindex="1" class="keyInput" data-ng-keydown="$ctrl.keyUp($event)"></div>\r\n\r\n    <div class="header">\r\n\r\n        <span class="buttons pull-right visible-sm visible-md visible-lg">\r\n\r\n                <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-sm" data-ng-click="$ctrl.toFirst()">\r\n                    {{ $ctrl.selected + 1 }} | {{ $ctrl.files.length }}\r\n                </button>\r\n\r\n                <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-sm" data-ng-click="$ctrl.toBackward()">\r\n                    <span class="glyphicon glyphicon-chevron-left"></span>\r\n                </button>\r\n\r\n                <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-sm" data-ng-click="$ctrl.toForward()">\r\n                    <span class="glyphicon glyphicon-chevron-right"></span>\r\n                </button>\r\n\r\n             <span data-ng-if="!$ctrl.isSingle" class="dropdown">\r\n                    <button class="btn btn-default btn-sm dropdown-toggle ng-binding" type="button" data-toggle="dropdown">\r\n                        <span class="glyphicon glyphicon-menu-hamburger"></span>\r\n                    </button>\r\n                    <ul class="dropdown-menu pull-right">\r\n                        <li class="dropdown-submenu">\r\n                            <a class="transitions" tabindex="-1" href="#">Transitions <span class="caret"></span></a>\r\n                            <ul class="dropdown-menu">\r\n                                <li><a tabindex="-1" href="#" data-ng-click="$ctrl.setTransition(transition)" data-ng-repeat="transition in $ctrl.transitions">\r\n                                    <span data-ng-class="{\'color-highlight\' : transition == $ctrl.transition}">\r\n                                        {{ transition }}</span></a>\r\n                                </li>\r\n                            </ul>\r\n                        </li>\r\n                        <li><a href="#" tabindex="-1" data-ng-click="$ctrl.toggleHelp()">Help</a></li>\r\n                    </ul>\r\n                </span>\r\n\r\n\r\n                <button class="btn btn-default btn-sm" data-ng-click="$ctrl.toggleGUI()">\r\n                    <span data-ng-if="$ctrl.gui" class="glyphicon glyphicon-chevron-up"></span>\r\n                    <span data-ng-if="!$ctrl.gui" class="glyphicon glyphicon-chevron-down"></span>\r\n                </button>\r\n\r\n                <button class="btn btn-default btn-sm" data-ng-click="$ctrl.toggleFullScreen()">\r\n                    <span class="glyphicon glyphicon-fullscreen"></span>\r\n                </button>\r\n\r\n                <button class="btn btn-default btn-sm" data-ng-click="$ctrl.exit()">\r\n                    <span class="glyphicon glyphicon-remove"></span>\r\n                </button>\r\n\r\n            </span>\r\n\r\n        <span class="buttons pull-right visible-xs">\r\n\r\n                    <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-xs" data-ng-click="$ctrl.toFirst()">\r\n                        {{ $ctrl.selected + 1 }} | {{ $ctrl.files.length }}\r\n                    </button>\r\n\r\n                    <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-xs" data-ng-click="$ctrl.toBackward()">\r\n                        <span class="glyphicon glyphicon-chevron-left"></span>\r\n                    </button>\r\n\r\n                    <button data-ng-if="!$ctrl.isSingle" class="btn btn-default btn-xs" data-ng-click="$ctrl.toForward()">\r\n                        <span class="glyphicon glyphicon-chevron-right"></span>\r\n                    </button>\r\n\r\n                    <span data-ng-if="!$ctrl.isSingle" class="dropdown">\r\n                        <button class="btn btn-default btn-xs dropdown-toggle ng-binding" type="button" data-toggle="dropdown">\r\n                            <span class="glyphicon glyphicon-menu-hamburger"></span>\r\n                        </button>\r\n                        <ul class="dropdown-menu pull-right">\r\n                            <li class="dropdown-submenu">\r\n                                <a class="transitions" tabindex="-1" href="#">Transitions <span class="caret"></span></a>\r\n                                <ul class="dropdown-menu">\r\n                                    <li><a tabindex="-1" href="#" data-ng-click="$ctrl.setTransition(transition)" data-ng-repeat="transition in $ctrl.transitions">\r\n                                        <span data-ng-class="{\'color-highlight\' : transition == $ctrl.transition}">\r\n                                            {{ transition }}</span></a>\r\n                                    </li>\r\n                                </ul>\r\n                            </li>\r\n                            <li><a href="#" tabindex="-1" data-ng-click="$ctrl.toggleHelp()">Help</a></li>\r\n                        </ul>\r\n                    </span>\r\n\r\n                    <button class="btn btn-default btn-xs" data-ng-click="$ctrl.toggleGUI()">\r\n                        <span data-ng-if="$ctrl.gui" class="glyphicon glyphicon-chevron-up"></span>\r\n                        <span data-ng-if="!$ctrl.gui" class="glyphicon glyphicon-chevron-down"></span>\r\n                    </button>\r\n\r\n                    <button class="btn btn-default btn-xs" data-ng-click="$ctrl.toggleFullScreen()">\r\n                        <span class="glyphicon glyphicon-fullscreen"></span>\r\n                    </button>\r\n\r\n                    <button class="btn btn-default btn-xs" data-ng-click="$ctrl.exit()">\r\n                        <span class="glyphicon glyphicon-remove"></span>\r\n                    </button>\r\n\r\n                </span>\r\n\r\n        <h2 class="visible-md visible-lg">\r\n            {{ $ctrl.title }} <span data-ng-if="$ctrl.subtitle" class="color-default">{{ $ctrl.subtitle }}</span>\r\n        </h2>\r\n\r\n        <h3 class="visible-sm">\r\n            {{ $ctrl.title }} <span data-ng-if="$ctrl.subtitle" class="color-default">{{ $ctrl.subtitle }}</span>\r\n        </h3>\r\n\r\n        <h4 class="visible-xs">\r\n            {{ $ctrl.title }} <span data-ng-if="$ctrl.subtitle" class="color-default">{{ $ctrl.subtitle }}</span>\r\n        </h4>\r\n\r\n    </div>\r\n\r\n    <div class="help text-right" data-ng-click="$ctrl.toggleHelp()" data-ng-show="$ctrl.help">\r\n        SPACE : forward<br>\r\n        RIGHT : forward<br>\r\n        LEFT : backward<br>\r\n        UP : first<br>\r\n        DOWN : last<br>\r\n        ESC : exit<br>\r\n        T : change transition<br>\r\n        F : toggle fullscreen<br>\r\n        G : toggle gui<br>\r\n        H : toggle help\r\n    </div>\r\n\r\n    <div class="images {{$ctrl.direction}} {{$ctrl.transition }}" data-ng-mouseover="$ctrl.functionsShow()" data-ng-mouseleave="$ctrl.functionsHide()" data-ng-swipe-left="$ctrl.toForward()" data-ng-swipe-right="$ctrl.toBackward()">\r\n\r\n        <div class="img" data-ng-repeat="(key,file) in $ctrl.files" data-ng-show="$ctrl.selected == key" data-ng-class="{\'loading\' : !file.loaded}" data-ng-style="{\'background-image\': \'url({{ file.source }})\'}">\r\n\r\n            <img style="height: 100%; width: 100%; object-fit: contain" data-ng-src="{{ file.source }}" alt="{{ file.title }}">\r\n\r\n            <div class="description" data-ng-show="$ctrl.functionsVisible">\r\n                <a href="{{ $ctrl.downloadLink() }}" target="_blank" class="btn btn-default btn-xs">\r\n                    <span class="glyphicon glyphicon-picture"></span> {{ $ctrl.file.description ?\r\n                    $ctrl.file.description : $ctrl.file.url }}\r\n                    | {{ $ctrl.file.size|bytes }}\r\n                </a>\r\n            </div>\r\n        </div>\r\n\r\n        <div data-ng-if="!$ctrl.isSingle" data-ng-show="$ctrl.functionsVisible" class="toBackward">\r\n\r\n            <button class="btn btn-default btn-lg pull-left visible-md visible-lg" data-ng-click="$ctrl.toBackward()">\r\n                <span class="glyphicon glyphicon-chevron-left"></span>\r\n            </button>\r\n\r\n            <button class="btn btn-default btn-md pull-left visible-sm" data-ng-click="$ctrl.toBackward()">\r\n                <span class="glyphicon glyphicon-chevron-left"></span>\r\n            </button>\r\n\r\n            <button class="btn btn-default btn-sm pull-left visible-xs" data-ng-click="$ctrl.toBackward()">\r\n                <span class="glyphicon glyphicon-chevron-left"></span>\r\n            </button>\r\n\r\n        </div>\r\n\r\n        <div data-ng-if="!$ctrl.isSingle" data-ng-show="$ctrl.functionsVisible" class="toForward">\r\n\r\n            <button class="btn btn-default btn-lg pull-right visible-md visible-lg" data-ng-click="$ctrl.toForward()">\r\n                <span class="glyphicon glyphicon-chevron-right"></span>\r\n            </button>\r\n\r\n            <button class="btn btn-default btn-md pull-right visible-sm" data-ng-click="$ctrl.toForward()">\r\n                <span class="glyphicon glyphicon-chevron-right"></span>\r\n            </button>\r\n\r\n            <button class="btn btn-default btn-sm pull-right visible-xs" data-ng-click="$ctrl.toForward()">\r\n                <span class="glyphicon glyphicon-chevron-right"></span>\r\n            </button>\r\n\r\n        </div>\r\n\r\n        <div data-ng-show="$ctrl.functionsVisible" class="functions">\r\n        </div>\r\n\r\n    </div>\r\n\r\n\r\n</div>\r\n\r\n');}]);